import { useEffect, useRef } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';

// Components (Pages)
import DashboardPage from './Pages/Dashboard/DashboardPage'

import Menubar from './Components/Menubar';
import Alert from './Components/Alert';
import Confirm from './Components/Confirm';
import NotFound from './Pages/NotFound/NotFound';
import SettingsPage, { changeTheme } from './Pages/Settings/SettingsPage';
import ExamTimetablePage from './Pages/ExamTimetable/ExamTimetablePage';
import PublishedTimetablePage from './Pages/ExamTimetable/PublishedTimetablePage';

// Styles
import './App.css'
import "./Style/BasicComponents.css"
import "./Style/UnifiedPages.css"

// Scripts

import { addWindowCloseEventHandler, removeWindowCloseEventHandler } from './Script/commonJS';

// Contexts
import { AlertProvider, useAlert } from './Components/AlertContextProvider';
import { ConfirmProvider, useConfirm } from './Components/ConfirmContextProvider';


function App() {
	return (
		<AlertProvider>
			<ConfirmProvider>
				<BrowserRouter>
					<MainApp />
				</BrowserRouter>
			</ConfirmProvider>
		</AlertProvider>
	)
}

function MainApp() {
	const app = useRef(null)

	const { showWarningConfirm } = useConfirm()
	const { showError } = useAlert()

	function autoToggleInResize() {
		if (window.innerWidth <= 1250) {
			if (app.current)
				app.current.classList.add("active");
		} else {
			if (app.current)
				app.current.classList.remove("active");
		}
	}

	useEffect(() => {
		autoToggleInResize();
		window.addEventListener("resize", () => {
			autoToggleInResize()
		})
		addWindowCloseEventHandler(showWarningConfirm, showError)

		// Initialize Theme from LocalStorage
		const savedTheme = localStorage.getItem('theme') || 'Light';
		changeTheme(savedTheme);

		return () => {
			window.removeEventListener("resize", () => {
				autoToggleInResize()
			})
			removeWindowCloseEventHandler()
		}
	}, [])

	return (
		<div className='app light' ref={app}>
			<Alert />
			<Confirm />
			<Menubar />

			<div className='main-container'>
				<Routes>
					<Route path="/" element={<DashboardPage />} />

					<Route path="/ExamTimetable" element={<ExamTimetablePage />} />
					<Route path="/ViewTimetable" element={<PublishedTimetablePage />} />
					<Route path="/Settings" element={<SettingsPage />} />
					<Route path="*" element={<NotFound />} />
				</Routes>
			</div>
		</div>
	)
}

export default App

// AlertContext.js
import { createContext, useState, useContext, useCallback } from 'react';

const AlertContext = createContext({
    alert: { message: '', type: 'warning', show: false },
    showAlert: () => { },
    hideAlert: () => { },
    showWarning: () => { },
    showSuccess: () => { },
    showError: () => { }
});

export const useAlert = () => {
    return useContext(AlertContext);
};

export const AlertProvider = ({ children }) => {
    const [alert, setAlert] = useState({ message: '', type: '', show: false });

    const showWarning = (message) => {
        setAlert({ message, type: 'warning', show: true });
        autoCloseAlert();
    };

    const showSuccess = (message) => {
        setAlert({ message, type: 'success', show: true });
        autoCloseAlert();
    }

    const showError = (message) => {
        setAlert({ message, type: 'error', show: true });
        autoCloseAlert();
    }

    const showAlert = (message, type) => {
        setAlert({ message, type, show: true });
        autoCloseAlert();
    };

    const hideAlert = () => {
        setAlert({ ...alert, show: false });
    };

    const deboucer = useCallback((func, delay) => {
        let timer = null;
        return function () {
            if (timer !== null) clearTimeout(timer);
            timer = setTimeout(() => {
                func();
            }, delay);
        }
    }, [])

    const autoCloseAlert = useCallback(deboucer(() => {
        hideAlert();
    }, 5000), []);

    return (
        <AlertContext.Provider value={{ alert, showAlert, hideAlert, showWarning, showSuccess, showError }}>
            {children}
        </AlertContext.Provider>
    );
};

import Plus from "../Icons/Plus";
import "../Style/Cards.css"
import Arrow from '../Icons/Arrow'
import { memo, useCallback, useEffect, useRef, useState } from "react";
import EditIcon from "../Icons/Edit";

const Cards = ({
    cardList = [],
    cardClassName = "",
    onCardClick = () => { },
    onAddBtnClick = () => { },
    canStayActiveMultipleCards = false,
    showEditBtn = false,
    onEditBtnClick = () => { },
    onActive = () => { }
}) => {
    const [activeCards, setActiveCards] = useState([])

    const defaultCardClickHandler = useCallback((card) => {
        let newActiveCards = [...activeCards]
        if (canStayActiveMultipleCards) {
            if (activeCards.includes(card)) {
                newActiveCards = activeCards.filter(activeCard => activeCard !== card)
            } else {
                newActiveCards = [...activeCards, card]
            }
        } else {
            newActiveCards = [card]
        }
        setActiveCards(newActiveCards)
        onActive(newActiveCards)
    }, [activeCards, canStayActiveMultipleCards, onActive])

    return (
        <div className="cards-container">
            <div className={"card add " + cardClassName + " add-new"} onClick={(e) => {
                setActiveCards([])
                onAddBtnClick(e)
            }}>
                <Plus />
            </div>
            {cardList && cardList.length > 0 && cardList.map((card) => (
                <Card
                    details={card}
                    key={card}
                    className={cardClassName}
                    active={activeCards.includes(card)}
                    onClick={(card) => {
                        defaultCardClickHandler(card)
                        onCardClick(card)
                    }}
                    compressText={false}
                    showEditBtn={showEditBtn}
                    onEditBtnClick={onEditBtnClick}
                />
            ))}
        </div>
    )
}

export default memo(Cards)

export const Card = memo(({
    details = "Sample",
    className = "",

    active = false,
    onClick = () => { },
    compressText = false,
    showEditBtn = false,
    onEditBtnClick = () => { }
}) => {
    const innerCard = (
        <div className={"card data " + className + (active ? ' active' : '')} onClick={() => onClick(details)} title={details}>
            {compressText ? (details.length > 6 ? details.slice(0, 5) + ".." : details) : details}
        </div>
    )
    return (
        (showEditBtn ? (
            <div className="card-wrapper">
                <div className="edit-btn" onClick={() => { onEditBtnClick(details) }}><EditIcon /></div>
                {innerCard}
            </div>
        ) : innerCard)
    )
})

export const HorizentalCardsContainer = memo(({
    cardList = [],
    className = "",
    onChange = () => { },
    cardClassName = "",
    onCardClick = () => { },
    compressText,
    showEditBtn = false,
    onEditBtnClick,
    canStayActiveMultipleCards = false
}) => {
    const cardsContainer = useRef(null)
    const [showArrow, setShowArrow] = useState(false)
    const [activeCards, setActiveCards] = useState([])

    useEffect(() => {
        if (cardList.length > 0) {
            setActiveCards([cardList[0]])
            onCardClick(cardList[0])
        }
    }, [cardList, onCardClick])

    const defaultCardClickHandler = useCallback((card) => {
        let newActiveCards = [...activeCards]
        if (canStayActiveMultipleCards) {
            if (activeCards.includes(card)) {
                newActiveCards = activeCards.filter(activeCard => activeCard !== card)
            } else {
                newActiveCards = [...activeCards, card]
            }
        } else {
            newActiveCards = [card]
        }
        setActiveCards(newActiveCards)
        onChange(newActiveCards)
    }, [activeCards, canStayActiveMultipleCards, onChange])

    const horizentalCardsOnWheelHandler = useCallback((event) => {
        if (cardsContainer.current == null) return
        cardsContainer.current.scrollLeft += (event.deltaY);
        showLeftArrow()
    }, [])
    const arrowClickHandler = useCallback((value) => {
        if (cardsContainer.current == null) return
        cardsContainer.current.scrollLeft += value;
        showLeftArrow()
    }, [])
    const showLeftArrow = useCallback(() => {
        if (cardsContainer.current == null) return
        if (cardsContainer.current.scrollLeft >= 120) {
            setShowArrow(true)
        } else {
            setShowArrow(false)
        }
    }, [])

    return (
        <div className={'horizental-cards-container ' + className} onWheel={horizentalCardsOnWheelHandler}>
            <Arrow className="left-arrow-for-scroll arrow-for-scroll" arrowStyle={{ zIndex: (showArrow ? "1" : "-10"), width: '1.5rem', height: '1.5rem' }} arrowIconClickHandler={() => { arrowClickHandler(-125) }} />
            <div className='sub-horizental-cards-container' ref={cardsContainer}>
                {cardList && cardList.length > 0 && cardList.map((card) => {
                    return (
                        <Card
                            details={card}
                            key={card}
                            className={cardClassName}
                            active={activeCards.includes(card)}
                            onClick={() => {
                                defaultCardClickHandler(card)
                                onCardClick(card)
                            }}
                            compressText={compressText}
                            showEditBtn={showEditBtn}
                            onEditBtnClick={onEditBtnClick}
                        />
                    );
                })}
            </div>
            <Arrow className="right-arrow-for-scroll arrow-for-scroll" arrowStyle={{ width: '1.5rem', height: '1.5rem' }} arrowIconClickHandler={() => { arrowClickHandler(125) }} />
        </div>
    )
})

// ConfirmContext.js
import { createContext, useState, useContext } from 'react';

const ConfirmContext = createContext({
    confirm: { message: '', type: 'warning', show: false, onApprove: () => { }, onDecline: () => { } },
    showConfirm: () => { },
    hideConfirm: () => { },
    showWarningConfirm: () => { },
    showSuccessConfirm: () => { },
    showErrorConfirm: () => { },
});

export const useConfirm = () => {
    return useContext(ConfirmContext);
};

export const ConfirmProvider = ({ children }) => {
    const [confirm, setConfirm] = useState({ message: '', type: '', show: false, onApprove: () => { }, onDecline: () => { } });

    const showWarningConfirm = (message, onApprove = () => { }, onDecline = () => { }) => {
        setConfirm({ message, type: 'warning', show: true, onApprove, onDecline });
    };

    const showSuccessConfirm = (message, onApprove = () => { }, onDecline = () => { }) => {
        setConfirm({ message, type: 'success', show: true, onApprove, onDecline });
    }

    const showErrorConfirm = (message, onApprove = () => { }, onDecline = () => { }) => {
        setConfirm({ message, type: 'error', show: true, onApprove, onDecline });
    }

    const showConfirm = (message, type, onApprove = () => { }, onDecline = () => { }) => {
        setConfirm({ message, type, show: true, onApprove, onDecline });
    };

    const hideConfirm = () => {
        setConfirm({ ...confirm, show: false });
    };

    return (
        <ConfirmContext.Provider value={{ confirm, showConfirm, hideConfirm, showWarningConfirm, showSuccessConfirm, showErrorConfirm }}>
            {children}
        </ConfirmContext.Provider>
    );
};

import { memo } from "react";
import { hasElement } from "../Script/util";
import "../Style/TimeTable.css"

export let emptyTimeTableDetails = [
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
    [[null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null], [null, null, null]],
]

const TimeTable = ({
    noOfPeriods = 9,
    breakTimeIndexs = [4],

    dayNames = ["Tue", "Wed", "Thu", "Fri", "Sat"],
    periodTimes = ["9:30AM", "10:20AM", "11:10AM", "12:00PM", "12:50PM", "01:40PM", "02:30PM", "03:20PM", "04:10PM"],

    details = emptyTimeTableDetails,

    subjectIndexAtPeriodElementInDetails = 1,
    subjectsDetails,

    periodClickHandler = () => { },

    className = "",
}) => {
    if (details.length <= 0) return <></>

    const breakWord = "BREAK";
    let dayRows = [];
    if (details)
        if (dayNames.length <= details.length && subjectsDetails) {
            for (let i = 0; i < dayNames.length; i++) {
                dayRows.push(
                    <DaysRow
                        key={i}
                        dayIndex={i}
                        noOfPeriods={noOfPeriods}
                        details={details[i]}
                        breakWord={breakWord[i]}
                        subjectsDetails={subjectsDetails}
                        breakTimeIndexs={breakTimeIndexs}
                        subjectIndexAtPeriodElementInDetails={subjectIndexAtPeriodElementInDetails}
                        dayName={dayNames[i]}
                        periodClickHandler={periodClickHandler} />
                )
            }
        } else {
            console.log(details, dayNames.length)
            dayRows.push(<div className="invalid-text" key={"error"}>Invalid Data</div>)
        }
    else dayRows.push(
        <div
            className="text"
            style={{ display: "grid", justifyContent: "center", alignItems: "center" }}
            key={"error"}>
            No Scedule Found <br />or <br />Time Table was not Generated Yet
        </div>
    )

    return (
        <div className={"time-table-container " + className}>
            <div className="period-times-container" style={{ gridTemplateColumns: `repeat(${noOfPeriods + 1},1fr)` }}>
                <div className="column-row-identifier">
                    <span className="column-indentifier">Day\
                        <span className="row-indentifier">Time</span>
                    </span>
                </div>
                {periodTimes && periodTimes.length > 0 && periodTimes.map((time) => (
                    <div className="time" key={time}>{time}</div>
                ))}
            </div>
            {dayRows}
        </div>
    )
}

const DaysRow = ({
    noOfPeriods = 9,
    dayIndex,
    details,
    breakWord,
    subjectsDetails,
    breakTimeIndexs,
    dayName,
    subjectIndexAtPeriodElementInDetails,
    periodClickHandler = () => { }
}) => {
    let periodCompIndex = 1
    let timeTableDataInteratorIndex = 0
    let infiniteLoopPreventerIndex = 0;

    let DayElements = [];
    if (!details) return <div className='time-table-error-text'>NO TIME TABLE FOUND / OR AN ERROR OCCURED</div>
    while (periodCompIndex <= noOfPeriods && timeTableDataInteratorIndex < details.length && infiniteLoopPreventerIndex < 50) {
        let periodDetails = details[timeTableDataInteratorIndex]
        if (hasElement(breakTimeIndexs, periodCompIndex)) {
            DayElements.push(<div key={`break-${periodCompIndex}`} className="period-details-container break">
                <div> </div>
                <div> {breakWord} </div>
                <div> </div>
            </div>)
        } else {
            if (periodDetails === null) {
                DayElements.push(<PeriodComp
                    key={timeTableDataInteratorIndex}
                    periodDetails={null}
                    dayIndex={dayIndex}
                    periodIndex={timeTableDataInteratorIndex}
                    onClick={() => { }} />)
            } else if (periodDetails !== null && periodDetails && periodDetails[subjectIndexAtPeriodElementInDetails]) {
                const isLab = (periodDetails[subjectIndexAtPeriodElementInDetails].toUpperCase() === "Subject".toUpperCase())
                    || (periodDetails[subjectIndexAtPeriodElementInDetails].toUpperCase() === "Lab".toUpperCase())
                    || subjectsDetails[periodDetails[subjectIndexAtPeriodElementInDetails]] === undefined ?
                    false
                    : subjectsDetails[periodDetails[subjectIndexAtPeriodElementInDetails]].isPractical
                DayElements.push(<PeriodComp
                    key={timeTableDataInteratorIndex}
                    periodDetails={periodDetails}
                    dayIndex={dayIndex}
                    periodIndex={timeTableDataInteratorIndex}
                    isLab={isLab}
                    onClick={periodClickHandler} />)
                if (isLab) {
                    timeTableDataInteratorIndex += 2;
                    periodCompIndex += 2;
                }
            } else DayElements.push(<PeriodComp
                key={timeTableDataInteratorIndex}
                periodDetails={periodDetails}
                dayIndex={dayIndex}
                periodIndex={timeTableDataInteratorIndex}
                onClick={periodClickHandler} />)
        }
        timeTableDataInteratorIndex++
        periodCompIndex++

        infiniteLoopPreventerIndex++
        if (infiniteLoopPreventerIndex === 49) {
            DayElements = []
            DayElements.push(
                <div key={`error-count-${infiniteLoopPreventerIndex}`}>Error In Data</div>
            )
        }
    }
    return (
        <div className="day-container" style={{ gridTemplateColumns: `repeat(${noOfPeriods + 1},1fr)` }}>
            <div className="day-name">{dayName}</div>
            {DayElements}
        </div>
    )
}

const PeriodComp = ({ periodDetails = [], dayIndex, periodIndex, isLab = false, onClick = () => { } }) => {
    let modifiedPeriodDetails;
    if (periodDetails) {
        modifiedPeriodDetails = [...periodDetails];
        if (modifiedPeriodDetails && modifiedPeriodDetails.length > 1) {
            const temp = modifiedPeriodDetails[0];
            modifiedPeriodDetails[0] = modifiedPeriodDetails[1];
            modifiedPeriodDetails[1] = temp;
        }
    }

    return (
        <div className="period-details-container class" style={isLab ? { gridColumn: 'auto / span 3' } : {}} onClick={() => onClick(dayIndex, periodIndex)}>
            {modifiedPeriodDetails && modifiedPeriodDetails?.length > 0 && modifiedPeriodDetails.map((detail, index) => (
                <div key={index} title={detail || ''}>{detail}</div>
            ))}
            {!periodDetails && <>
                <div>&nbsp;</div>
                <div>&nbsp;</div>
                <div>&nbsp;</div>
            </>}
        </div>
    )
}

export default memo(TimeTable)

