import { memo, useCallback, useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import Trash from '../../Icons/Trash';
import EditIcon from '../../Icons/Edit';
import SearchIcon from '../../Icons/Search';
import ExamTimetableIcon from '../../Icons/ExamTimetableIcon';
import CrossIcon from '../../Icons/Cross';
import CalendarPlus from '../../Icons/CalendarPlus';
import '../../Style/Pages/ExamTimetablePage.css';
import { useAlert } from '../../Components/AlertContextProvider';
import { useConfirm } from '../../Components/ConfirmContextProvider';
import Loader from '../../Components/Loader';
import {
    getExams,
    scheduleExam,
    updateExam,
    deleteExam,
    checkConflicts,
    autoResolveConflicts,
    publishTimetable,
    formatTo12Hour
} from '../../Script/ExamDataFetcher';
import { checkDbConnection } from '../../Script/HealthFetcher';
import { getSubjectsDetailsList, saveSubject } from '../../Script/SubjectsDataFetcher';

const TIME_SLOTS = [
    { label: '9:30 AM - 11:00 AM', start: '09:30', end: '11:00', duration: 90 },
    { label: '1:30 PM - 3:00 PM', start: '13:30', end: '15:00', duration: 90 }
];

const SEMESTERS = [1, 2, 3, 4, 5, 6, 7, 8];
const DEPARTMENTS = ['CSE', 'ECE', 'EEE', 'ME', 'CE', 'IT', 'AIML'];



function ExamTimetablePage() {
    const [exams, setExams] = useState([]);
    const [conflicts, setConflicts] = useState([]);
    const [isConflictFree, setIsConflictFree] = useState(true);
    const [loading, setLoading] = useState(false);
    const [subjectsDetails, setSubjectsDetails] = useState([]);
    const [editingExam, setEditingExam] = useState(null);

    // Modal State
    const [showAddSubjectModal, setShowAddSubjectModal] = useState(false);
    const [newSubjectName, setNewSubjectName] = useState('');
    const [newSubjectCode, setNewSubjectCode] = useState('');
    const [newSubjectType, setNewSubjectType] = useState('Theory');

    // Note: Subject entity doesn't have department, so we only save semantic fields


    // Form state
    const [selectedSemester, setSelectedSemester] = useState(1);
    const [selectedDepartment, setSelectedDepartment] = useState('CSE');
    const [selectedCourse, setSelectedCourse] = useState('');
    const [examDate, setExamDate] = useState('');
    const [selectedTimeSlot, setSelectedTimeSlot] = useState(0);
    const [hallId, setHallId] = useState('');
    const [testCoordinator, setTestCoordinator] = useState(() => localStorage.getItem('testCoordinator') || '');
    const [showCourseDropdown, setShowCourseDropdown] = useState(false);
    const [showDeptDropdown, setShowDeptDropdown] = useState(false);
    const [showSemDropdown, setShowSemDropdown] = useState(false);
    const [showTimeSlotDropdown, setShowTimeSlotDropdown] = useState(false);
    const [showExamTypeDropdown, setShowExamTypeDropdown] = useState(false);
    const [examType, setExamType] = useState('MSE I');

    // Search state
    const [searchQuery, setSearchQuery] = useState('');

    // Filter state
    const [filterSemester, setFilterSemester] = useState();
    const [filterDepartment, setFilterDepartment] = useState('');

    const { showError, showSuccess } = useAlert();
    const { showErrorConfirm } = useConfirm();
    const location = useLocation();

    // Load initial data
    useEffect(() => {
        loadExams();
        getSubjectsDetailsList(data => setSubjectsDetails(data));
    }, []);

    // Check for edit state from navigation
    useEffect(() => {
        if (location.state?.editExam) {
            handleEditExam(location.state.editExam);
            // Clear state to prevent re-triggering on refresh? 
            // Actually router state persists, but handleEditExam just sets state, so it's idempotent-ish.
            // Ideally we should clear it, but modifying history might be overkill. 
            // Proceeding with just population.
        }
    }, [location.state]);

    // Reload when filters change
    useEffect(() => {
        loadExams();
    }, [filterSemester, filterDepartment]);

    useEffect(() => {
        localStorage.setItem('testCoordinator', testCoordinator);
    }, [testCoordinator]);

    // Check conflicts whenever exams change
    useEffect(() => {
        if (exams.length > 0) {
            loadConflicts();
        } else {
            setConflicts([]);
            setIsConflictFree(true);
        }
    }, [exams]);

    const loadExams = async () => {
        const data = await getExams(filterSemester, filterDepartment || undefined, 'DRAFT');
        setExams(data);
    };

    const loadConflicts = async () => {
        const result = await checkConflicts(filterSemester, filterDepartment || undefined);
        setConflicts(result.conflicts);
        setIsConflictFree(result.conflictFree);
    };

    const handleScheduleExam = useCallback(async () => {
        const isConnected = await checkDbConnection();
        if (!isConnected) {
            showError("Database is not connected. Cannot schedule exam.");
            return;
        }

        if (!selectedCourse || !examDate) {
            showError('Please fill all required fields');
            return;
        }

        // Check if there are already 2 exams for this date, semester, and department
        const examsOnSameDay = exams.filter(exam =>
            exam.examDate === examDate &&
            exam.semester === selectedSemester &&
            exam.department === selectedDepartment
        );

        if (examsOnSameDay.length >= 2) {
            showError(`Maximum 2 subjects allowed per day for ${selectedDepartment} Semester ${selectedSemester}. This date already has ${examsOnSameDay.length} exam(s) scheduled.`);
            return;
        }

        const slot = TIME_SLOTS[selectedTimeSlot];
        const request = {
            semester: selectedSemester,
            courseName: selectedCourse,
            examDate: examDate,
            startTime: slot.start,
            endTime: slot.end,
            durationMinutes: slot.duration,
            hallId: hallId || undefined,
            department: selectedDepartment,
            testCoordinator: testCoordinator || undefined,
            hod: `Dept. of ${selectedDepartment}`,
            examType: examType
        };

        setLoading(true);
        scheduleExam(
            request,
            () => {
                showSuccess('Exam scheduled successfully');
                loadExams();
                setSelectedCourse('');
                setLoading(false);
            },
            (msg) => {
                showError(msg);
                setLoading(false);
            }
        );
    }, [selectedSemester, selectedDepartment, selectedCourse, examDate, selectedTimeSlot, hallId, testCoordinator, examType, exams, showError, showSuccess, loadExams]);

    const handleUpdateExam = useCallback(async () => {
        if (!editingExam) return;

        const isConnected = await checkDbConnection();
        if (!isConnected) {
            showError("Database is not connected. Cannot update exam.");
            return;
        }

        if (!examDate) {
            showError('Please fill all required fields');
            return;
        }

        const slot = TIME_SLOTS[selectedTimeSlot];
        updateExam(
            editingExam.id,
            {
                semester: selectedSemester,
                department: selectedDepartment,
                courseName: selectedCourse,
                examDate: examDate,
                startTime: slot.start,
                endTime: slot.end,
                hallId: hallId,
                durationMinutes: slot.duration,
                testCoordinator: testCoordinator || undefined,
                hod: `Dept. of ${selectedDepartment}`,
                examType: examType
            },
            () => {
                showSuccess('Exam updated');
                loadExams();
                setEditingExam(null);
                resetForm();
            },
            showError
        );
    }, [editingExam, examDate, selectedTimeSlot, hallId, showError, showSuccess, loadExams]);

    const handleDeleteExam = useCallback((id) => {
        console.log('Delete button clicked for exam ID:', id);

        showErrorConfirm(' Permanently delete this exam?', () => {
            console.log('Confirmation approved, calling deleteExam for ID:', id);
            deleteExam(
                id,
                () => {
                    console.log('Delete successful!');
                    showSuccess('Exam deleted successfully');
                    loadExams();
                },
                (msg) => {
                    console.error('Delete failed:', msg);
                    showError('Failed to delete exam: ' + msg);
                }
            );
        });
    }, [showError, showSuccess, showErrorConfirm, loadExams]);

    const handleEditExam = (exam) => {
        setEditingExam(exam);
        setSelectedSemester(exam.semester);
        setSelectedDepartment(exam.department || 'CSE');
        setSelectedCourse(exam.courseName);
        setExamDate(exam.examDate);
        setHallId(exam.hallId || '');
        setTestCoordinator(exam.testCoordinator || '');
        setExamType(exam.examType || 'MSE I');
        // Find matching time slot
        const slotIdx = TIME_SLOTS.findIndex(s => s.start === exam.startTime);
        setSelectedTimeSlot(slotIdx >= 0 ? slotIdx : 0);
    };

    const resetForm = () => {
        setSelectedCourse('');
        setExamDate('');
        setHallId('');
        setTestCoordinator('');
        setExamType('MSE I');
        setSelectedTimeSlot(0);
        setEditingExam(null);
    };

    const handleAutoResolve = useCallback(async () => {
        const isConnected = await checkDbConnection();
        if (!isConnected) {
            showError("Database is not connected. Cannot resolve conflicts.");
            return;
        }

        setLoading(true);
        autoResolveConflicts(
            filterSemester,
            filterDepartment || undefined,
            (data) => {
                showSuccess(data.message);
                loadExams();
                setLoading(false);
            },
            (msg) => {
                showError(msg);
                setLoading(false);
            }
        );
    }, [filterSemester, filterDepartment, showError, showSuccess, loadExams]);

    const handlePublish = useCallback(async () => {
        const isConnected = await checkDbConnection();
        if (!isConnected) {
            showError("Database is not connected. Cannot publish timetable.");
            return;
        }

        if (!isConflictFree) {
            showError('Cannot publish: Please resolve all conflicts first');
            return;
        }

        setLoading(true);
        publishTimetable(
            filterSemester,
            filterDepartment || undefined,
            (data) => {
                showSuccess(data.message);
                loadExams();
                setLoading(false);
            },
            (msg) => {
                showError(msg);
                setLoading(false);
            }
        );
    }, [filterSemester, filterDepartment, isConflictFree, showError, showSuccess, loadExams]);



    // Get courses for selected department and semester
    // Strict filtering - only show courses that match BOTH semester AND department
    const coursesForSemester = subjectsDetails.filter(s =>
        s.sem === selectedSemester &&
        s.department === selectedDepartment
    );

    const handleCourseChange = (e) => {
        const val = e.target.value;
        if (val === '__NEW__') {
            setShowAddSubjectModal(true);
            // Reset modal fields
            setNewSubjectName('');
            setNewSubjectCode('');
            setNewSubjectType('Theory');

        } else {
            setSelectedCourse(val);
        }
    };

    const handleSaveNewSubject = async () => {
        if (!newSubjectName || !newSubjectCode) {
            showError("Please enter Subject Name and Code");
            return;
        }

        const newSub = {
            name: newSubjectName,
            subjectCode: newSubjectCode,
            sem: selectedSemester,
            department: selectedDepartment,
            subjectType: newSubjectType
        };

        setLoading(true);
        saveSubject(
            newSubjectName,
            newSub,
            () => {
                showSuccess("Subject added successfully");
                // Refresh subjects
                getSubjectsDetailsList(data => setSubjectsDetails(data));
                setSelectedCourse(newSubjectName); // Auto-select the new subject
                setShowAddSubjectModal(false);
                setLoading(false);
            },
            (msg) => {
                showError("Failed to add subject: " + msg);
                setLoading(false);
            }
        );
    };

    // Filter exams based on search query
    const filteredExams = exams.filter(exam =>
        exam.courseName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (exam.department && exam.department.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    // Group exams by date for display
    const examsByDate = filteredExams.reduce((acc, exam) => {
        if (!acc[exam.examDate]) acc[exam.examDate] = [];
        acc[exam.examDate].push(exam);
        return acc;
    }, {});

    const conflictExamIds = new Set(conflicts.flatMap(c => [c.examId1, c.examId2]));

    return (
        <div className='page exam-timetable'>
            <div className='page-header'>
                <h1 className='page-title'>
                    <span className='title-icon' style={{ display: 'inline-flex', verticalAlign: 'middle', marginRight: '8px' }}>
                        <ExamTimetableIcon size={32} color="#2563eb" />
                    </span>
                    Examination Timetable
                </h1>
                <div className='header-actions'>
                    <div className="exam-search-container">
                        <span className="exam-search-icon" style={{ display: 'flex', alignItems: 'center' }}>
                            <div style={{ width: '16px', height: '16px' }}>
                                <SearchIcon fillColor="#64748b" />
                            </div>
                        </span>
                        <input
                            type="text"
                            className="exam-search-input"
                            placeholder="Search exams..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>
            </div>

            <div className='exam-content'>
                {/* Input Panel */}
                <div className='input-panel'>
                    <h2 style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        {editingExam ? (
                            <><EditIcon width={22} height={22} /> Edit Exam</>
                        ) : (
                            <><CalendarPlus size={22} /> Schedule Exam</>
                        )}
                    </h2>

                    <div className='form-group'>
                        <label>Department *</label>
                        <div className="custom-select-container">
                            <div
                                className={`custom-select-trigger ${showDeptDropdown ? 'open' : ''}`}
                                onClick={() => setShowDeptDropdown(!showDeptDropdown)}
                            >
                                {selectedDepartment}
                            </div>

                            {showDeptDropdown && (
                                <>
                                    <div
                                        style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 99 }}
                                        onClick={() => setShowDeptDropdown(false)}
                                    />
                                    <div className="custom-select-options">
                                        {DEPARTMENTS.map(dept => (
                                            <div
                                                key={dept}
                                                className="custom-option"
                                                onClick={() => {
                                                    setSelectedDepartment(dept);
                                                    setShowDeptDropdown(false);
                                                }}
                                            >
                                                {dept}
                                            </div>
                                        ))}
                                    </div>
                                </>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>Semester *</label>
                        <div className="custom-select-container">
                            <div
                                className={`custom-select-trigger ${showSemDropdown ? 'open' : ''}`}
                                onClick={() => setShowSemDropdown(!showSemDropdown)}
                            >
                                Semester {selectedSemester}
                            </div>

                            {showSemDropdown && (
                                <>
                                    <div
                                        style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 99 }}
                                        onClick={() => setShowSemDropdown(false)}
                                    />
                                    <div className="custom-select-options">
                                        {SEMESTERS.map(sem => (
                                            <div
                                                key={sem}
                                                className="custom-option"
                                                onClick={() => {
                                                    setSelectedSemester(sem);
                                                    setShowSemDropdown(false);
                                                }}
                                            >
                                                Semester {sem}
                                            </div>
                                        ))}
                                    </div>
                                </>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>Course *</label>
                        <div className="custom-select-container">
                            <div
                                className={`custom-select-trigger ${showCourseDropdown ? 'open' : ''}`}
                                onClick={() => setShowCourseDropdown(!showCourseDropdown)}
                            >
                                {selectedCourse ? (
                                    (() => {
                                        const c = coursesForSemester.find(c => c.name === selectedCourse);
                                        return c ? `${c.name} (${c.subjectCode})` : selectedCourse;
                                    })()
                                ) : (
                                    <span style={{ color: 'var(--textColor2)' }}>Select a course</span>
                                )}
                            </div>

                            {showCourseDropdown && (
                                <>
                                    <div
                                        style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 99 }}
                                        onClick={() => setShowCourseDropdown(false)}
                                    />
                                    <div className="custom-select-options">
                                        <div
                                            className="custom-option"
                                            onClick={() => {
                                                setSelectedCourse('');
                                                setShowCourseDropdown(false);
                                            }}
                                        >
                                            Select a course
                                        </div>
                                        {coursesForSemester.map(course => (
                                            <div
                                                key={course.name}
                                                className="custom-option"
                                                onClick={() => {
                                                    setSelectedCourse(course.name);
                                                    setShowCourseDropdown(false);
                                                }}
                                            >
                                                {course.name} ({course.subjectCode})
                                            </div>
                                        ))}
                                        <div
                                            className="custom-option add-new"
                                            onClick={() => {
                                                handleCourseChange({ target: { value: '__NEW__' } });
                                                setShowCourseDropdown(false);
                                            }}
                                        >
                                            + Add New Subject
                                        </div>
                                    </div>
                                </>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>Exam Type *</label>
                        <div className="custom-select-container">
                            <div
                                className={`custom-select-trigger ${showExamTypeDropdown ? 'open' : ''}`}
                                onClick={() => setShowExamTypeDropdown(!showExamTypeDropdown)}
                            >
                                {examType}
                            </div>

                            {showExamTypeDropdown && (
                                <>
                                    <div
                                        style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 99 }}
                                        onClick={() => setShowExamTypeDropdown(false)}
                                    />
                                    <div className="custom-select-options">
                                        <div
                                            className="custom-option"
                                            onClick={() => {
                                                setExamType('MSE I');
                                                setShowExamTypeDropdown(false);
                                            }}
                                        >
                                            MSE I
                                        </div>
                                        <div
                                            className="custom-option"
                                            onClick={() => {
                                                setExamType('MSE II');
                                                setShowExamTypeDropdown(false);
                                            }}
                                        >
                                            MSE II
                                        </div>
                                    </div>
                                </>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>Exam Date *</label>
                        <input
                            type="date"
                            value={examDate}
                            onChange={e => setExamDate(e.target.value)}
                            min={new Date().toISOString().split('T')[0]}
                        />
                    </div>

                    <div className='form-group'>
                        <label>Time Slot *</label>
                        <div className="custom-select-container">
                            <div
                                className={`custom-select-trigger ${showTimeSlotDropdown ? 'open' : ''}`}
                                onClick={() => setShowTimeSlotDropdown(!showTimeSlotDropdown)}
                            >
                                {TIME_SLOTS[selectedTimeSlot]?.label || 'Select Time Slot'}
                            </div>

                            {showTimeSlotDropdown && (
                                <>
                                    <div
                                        style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, zIndex: 99 }}
                                        onClick={() => setShowTimeSlotDropdown(false)}
                                    />
                                    <div className="custom-select-options">
                                        {TIME_SLOTS.map((slot, idx) => (
                                            <div
                                                key={idx}
                                                className="custom-option"
                                                onClick={() => {
                                                    setSelectedTimeSlot(idx);
                                                    setShowTimeSlotDropdown(false);
                                                }}
                                            >
                                                {slot.label}
                                            </div>
                                        ))}
                                    </div>
                                </>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>Test Coordinator</label>
                        <div style={{ position: 'relative', display: 'flex', alignItems: 'center' }}>
                            <input
                                type="text"
                                value={testCoordinator}
                                onChange={e => setTestCoordinator(e.target.value)}
                                placeholder="e.g., Dr. Uma R..."
                                style={{ paddingRight: '2rem' }}
                            />
                            {testCoordinator && (
                                <div
                                    onClick={() => setTestCoordinator('')}
                                    style={{
                                        position: 'absolute',
                                        right: '0.5rem',
                                        cursor: 'pointer',
                                        opacity: 0.5,
                                        display: 'flex',
                                        alignItems: 'center',
                                        padding: '4px'
                                    }}
                                    title="Clear"
                                >
                                    <CrossIcon size={12} />
                                </div>
                            )}
                        </div>
                    </div>

                    <div className='form-group'>
                        <label>HOD</label>
                        <input
                            type="text"
                            value={`Dept. of ${selectedDepartment}`}
                            readOnly
                            style={{ backgroundColor: 'var(--bgColor2)', cursor: 'not-allowed' }}
                        />
                    </div>



                    <div className='form-buttons'>
                        {editingExam ? (
                            <>
                                <button className='btn primary' onClick={handleUpdateExam}>
                                     Update
                                </button>
                                <button className='btn secondary' onClick={resetForm}>
                                    Cancel
                                </button>
                            </>
                        ) : (
                            <button className='btn primary' onClick={handleScheduleExam}>
                                + Add Exam
                            </button>
                        )}
                    </div>
                </div>

                {/* Preview Panel */}
                <div className='preview-panel'>
                    <div className='panel-header'>
                        {/* <h2>Exam Schedule</h2> - Removed as per user request */}
                        <div className='filter-bar'>
                            <select
                                value={filterSemester || ''}
                                onChange={e => setFilterSemester(e.target.value ? Number(e.target.value) : undefined)}
                            >
                                <option value="">All Semesters</option>
                                {SEMESTERS.map(sem => (
                                    <option key={sem} value={sem}>Sem {sem}</option>
                                ))}
                            </select>
                            <select
                                value={filterDepartment}
                                onChange={e => setFilterDepartment(e.target.value)}
                            >
                                <option value="">All Depts</option>
                                {DEPARTMENTS.map(dept => (
                                    <option key={dept} value={dept}>{dept}</option>
                                ))}
                            </select>
                        </div>
                        <div className='status-badge' data-status={isConflictFree ? 'ok' : 'conflict'}>
                            {isConflictFree ? ' No Conflicts' : ` ${conflicts.length} Conflict(s)`}
                        </div>
                    </div>

                    {/* Conflict Alert */}
                    {!isConflictFree && (
                        <div className='conflict-alert'>
                            <h3> Conflicts Detected</h3>
                            <ul>
                                {conflicts.slice(0, 5).map((c, idx) => (
                                    <li key={idx} className={`conflict-${c.type.toLowerCase()}`}>
                                        <span className='conflict-type'>{c.type}</span>
                                        {c.message}
                                    </li>
                                ))}
                                {conflicts.length > 5 && <li>...and {conflicts.length - 5} more</li>}
                            </ul>
                            <button className='btn warning' onClick={handleAutoResolve}>
                                 Auto-Resolve Conflicts
                            </button>
                        </div>
                    )}

                    {/* Exam Table */}
                    <div className='exam-table-container'>
                        <table className='exam-table'>
                            <thead>
                                <tr>
                                    <th>Date</th>
                                    <th>Time</th>
                                    <th>Exam Type</th>
                                    <th>Dept</th>
                                    <th>Sem</th>
                                    <th>Course</th>
                                    <th>Status</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {Object.entries(examsByDate)
                                    .sort(([a], [b]) => a.localeCompare(b))
                                    .flatMap(([date, dateExams]) =>
                                        dateExams.map((exam, idx) => (
                                            <tr
                                                key={exam.id}
                                                className={`${conflictExamIds.has(exam.id) ? 'conflict-row' : ''} ${exam.status.toLowerCase()}`}
                                            >
                                                {idx === 0 && (
                                                    <td rowSpan={dateExams.length} className='date-cell'>
                                                        {new Date(date).toLocaleDateString('en-US', {
                                                            weekday: 'short',
                                                            month: 'short',
                                                            day: 'numeric'
                                                        })}
                                                    </td>
                                                )}
                                                <td className='time-cell'>{formatTo12Hour(exam.startTime)} - {formatTo12Hour(exam.endTime)}</td>
                                                <td style={{ textAlign: 'center' }}><span className='exam-type-badge'>{exam.examType || '-'}</span></td>
                                                <td><span className='dept-badge'>{exam.department || '-'}</span></td>
                                                <td><span className='sem-badge'>Sem {exam.semester}</span></td>
                                                <td className='course-cell'>{exam.courseName}</td>
                                                <td>
                                                    <span className={`status-tag ${exam.status.toLowerCase()}`}>
                                                        {exam.status}
                                                    </span>
                                                </td>
                                                <td className='actions-cell'>
                                                    {exam.status === 'DRAFT' && (
                                                        <div style={{ display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
                                                            <button
                                                                className='btn-icon'
                                                                onClick={() => handleEditExam(exam)}
                                                                title="Edit"
                                                                style={{ width: '32px', height: '32px', padding: '6px' }}
                                                            >
                                                                <EditIcon width={16} height={16} />
                                                            </button>
                                                            <button
                                                                className='btn-icon delete'
                                                                onClick={() => {
                                                                    console.log('TRASH BUTTON CLICKED!', exam.id);
                                                                    handleDeleteExam(exam.id);
                                                                }}
                                                                title="Delete"
                                                                style={{ width: '32px', height: '32px', padding: '6px' }}
                                                            >
                                                                <Trash width={16} height={16} fill="currentColor" />
                                                            </button>
                                                        </div>
                                                    )}
                                                </td>
                                            </tr>
                                        ))
                                    )}
                            </tbody>
                        </table>
                        {exams.length === 0 && (
                            <div className='empty-state'>
                                <p> No exams scheduled yet.</p>
                                <p>Use the form to add exams.</p>
                            </div>
                        )}
                    </div>

                    {/* Action Buttons */}
                    <div className='action-buttons' style={{ justifyContent: 'flex-end', marginTop: 'auto' }}>

                        <button
                            className='btn success'
                            onClick={handlePublish}
                            disabled={exams.length === 0 || !isConflictFree || exams.every(e => e.status === 'PUBLISHED')}
                        >
                             Publish Timetable
                        </button>
                    </div>
                </div>
            </div>

            {loading && <Loader />}

            {/* Add Subject Modal */}
            {showAddSubjectModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Add New Subject</h3>
                        <div className="form-group">
                            <label>Subject Name *</label>
                            <input
                                type="text"
                                value={newSubjectName}
                                onChange={e => setNewSubjectName(e.target.value)}
                                placeholder="e.g. Advanced Algorithms"
                            />
                        </div>
                        <div className="form-group">
                            <label>Subject Code *</label>
                            <input
                                type="text"
                                value={newSubjectCode}
                                onChange={e => setNewSubjectCode(e.target.value)}
                                placeholder="e.g. CS501"
                            />
                        </div>

                        <div className="modal-actions">
                            <button className="btn secondary" onClick={() => setShowAddSubjectModal(false)}>Cancel</button>
                            <button className="btn primary" onClick={handleSaveNewSubject}>Save & Select</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default memo(ExamTimetablePage);

import { memo, useEffect, useState } from 'react';
import '../../Style/Pages/ExamTimetablePage.css';
import { useAlert } from '../../Components/AlertContextProvider';
import { useConfirm } from '../../Components/ConfirmContextProvider';
import {
    getExams,
    printTimetable,
    downloadTimetable,
    formatTo12Hour,
    deleteExam,
    updateExam
} from '../../Script/ExamDataFetcher';
import Trash from '../../Icons/Trash';
import EditIcon from '../../Icons/Edit';
import { getSubjectsDetailsList } from '../../Script/SubjectsDataFetcher';
import SearchIcon from '../../Icons/Search';
import CertifiedDocument from '../../Icons/CertifiedDocument';
import Printer from '../../Icons/Printer';
import Download from '../../Icons/Download';

const SEMESTERS = [1, 2, 3, 4, 5, 6, 7, 8];
const DEPARTMENTS = ['CSE', 'ECE', 'EEE', 'ME', 'CE', 'IT', 'AIML'];

function PublishedTimetablePage() {
    const { showError, showSuccess } = useAlert();
    const { showErrorConfirm } = useConfirm();
    const [exams, setExams] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filterSemester, setFilterSemester] = useState();
    const [filterDepartment, setFilterDepartment] = useState('');
    const [filterExamType, setFilterExamType] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    // Edit modal state
    const [showEditModal, setShowEditModal] = useState(false);
    const [editingExam, setEditingExam] = useState(null);
    const [editDepartment, setEditDepartment] = useState('');
    const [editSemester, setEditSemester] = useState(1);
    const [editCourse, setEditCourse] = useState('');
    const [editDate, setEditDate] = useState('');
    const [editStartTime, setEditStartTime] = useState('');
    const [editEndTime, setEditEndTime] = useState('');
    const [_editRoomNo, setEditRoomNo] = useState('');
    const [editExamType, setEditExamType] = useState('MSE I');
    const [editTestCoordinator, setEditTestCoordinator] = useState('');
    const [subjectsDetails, setSubjectsDetails] = useState([]);

    // Print modal state
    const [showPrintModal, setShowPrintModal] = useState(false);
    const [printDepartment, setPrintDepartment] = useState('');
    const [printSemester, setPrintSemester] = useState();
    const [printExamType, setPrintExamType] = useState('');

    // Download modal state
    const [showDownloadModal, setShowDownloadModal] = useState(false);
    const [downloadDepartment, setDownloadDepartment] = useState('');
    const [downloadSemester, setDownloadSemester] = useState();
    const [downloadExamType, setDownloadExamType] = useState('');

    useEffect(() => {
        loadData();
        getSubjectsDetailsList(data => {
            const subjects = data;
            console.log('Loaded subjects:', subjects);
            setSubjectsDetails(subjects);
        });
    }, [filterSemester, filterDepartment]);

    const loadData = async () => {
        setLoading(true);
        try {
            const examData = await getExams(filterSemester, filterDepartment || undefined, 'PUBLISHED');
            setExams(examData);
        } catch (error) {
            console.error(error);
        } finally {
            setLoading(false);
        }
    };

    const handlePrint = () => {
        if (exams.length === 0) {
            showError('No exams available to print');
            return;
        }
        // Reset print filters and show modal
        setPrintDepartment('');
        setPrintSemester(undefined);
        setPrintExamType('');
        setShowPrintModal(true);
    };

    const handleConfirmPrint = () => {
        if (!printDepartment || !printSemester || !printExamType) {
            showError('Please select Department, Semester, and Exam Type');
            return;
        }

        // Filter exams based on selection
        const filteredExams = exams.filter(exam =>
            exam.department === printDepartment &&
            exam.semester === printSemester &&
            exam.examType === printExamType
        );

        if (filteredExams.length === 0) {
            showError(`No exams found for ${printDepartment} Semester ${printSemester} ${printExamType}`);
            return;
        }

        setShowPrintModal(false);
        printTimetable(filteredExams, subjectsDetails, 'Examination Timetable');
    };

    const handleDownload = () => {
        if (exams.length === 0) {
            showError('No exams available to download');
            return;
        }
        // Reset download filters and show modal
        setDownloadDepartment('');
        setDownloadSemester(undefined);
        setDownloadExamType('');
        setShowDownloadModal(true);
    };

    const handleConfirmDownload = async () => {
        if (!downloadDepartment || !downloadSemester || !downloadExamType) {
            showError('Please select Department, Semester, and Exam Type');
            return;
        }

        // Filter exams based on selection
        const filteredExams = exams.filter(exam =>
            exam.department === downloadDepartment &&
            exam.semester === downloadSemester &&
            exam.examType === downloadExamType
        );

        if (filteredExams.length === 0) {
            showError(`No exams found for ${downloadDepartment} Semester ${downloadSemester} ${downloadExamType}`);
            return;
        }

        setShowDownloadModal(false);

        // Direct PDF download with exact same format as print
        try {
            await downloadTimetable(filteredExams, subjectsDetails, 'Examination Timetable');
            showSuccess('PDF downloaded successfully!');
        } catch (error) {
            console.error('Download error:', error);
            showError('Failed to download PDF. Please try again.');
        }
    };

    const handleDelete = (id) => {
        showErrorConfirm(' Permanently delete this exam?', () => {
            console.log('Deleting exam with ID:', id);
            setLoading(true);
            deleteExam(
                id,
                () => {
                    console.log('Delete successful, reloading data');
                    showSuccess('Exam deleted successfully');
                    loadData();
                },
                (msg) => {
                    console.error('Delete failed:', msg);
                    showError('Failed to delete exam: ' + msg);
                    setLoading(false);
                }
            );
        });
    };

    const handleEdit = (exam) => {
        console.log('Editing exam:', exam);
        console.log('Available subjects:', subjectsDetails);
        console.log('Subjects for semester', exam.semester, ':', subjectsDetails.filter(s => s.sem === exam.semester));
        setEditingExam(exam);
        setEditDepartment(exam.department || 'CSE');
        setEditSemester(exam.semester);
        setEditCourse(exam.courseName);
        setEditDate(exam.examDate);
        setEditStartTime(exam.startTime);
        setEditEndTime(exam.endTime);
        setEditRoomNo(exam.hallId || '');
        setEditExamType(exam.examType || 'MSE I');
        setEditTestCoordinator(exam.testCoordinator || '');
        setShowEditModal(true);
    };

    const handleSaveEdit = async () => {
        if (!editingExam) return;

        if (!editDepartment || !editSemester || !editCourse || !editDate || !editStartTime || !editEndTime) {
            showError('Please fill all required fields');
            return;
        }

        // Calculate duration in minutes
        const start = editStartTime.split(':');
        const end = editEndTime.split(':');
        const startMinutes = parseInt(start[0]) * 60 + parseInt(start[1]);
        const endMinutes = parseInt(end[0]) * 60 + parseInt(end[1]);
        const durationMinutes = endMinutes - startMinutes;

        const updateData = {
            department: editDepartment,
            semester: editSemester,
            courseName: editCourse,
            examDate: editDate,
            startTime: editStartTime,
            endTime: editEndTime,
            durationMinutes: durationMinutes,
            examType: editExamType,
            testCoordinator: editTestCoordinator
        };

        console.log('Updating exam ID:', editingExam.id);
        console.log('Update data:', updateData);

        setLoading(true);
        updateExam(
            editingExam.id,
            updateData,
            (updatedExam) => {
                console.log('Exam updated successfully:', updatedExam);
                showSuccess('Exam updated successfully');
                setShowEditModal(false);
                setEditingExam(null);
                loadData();
            },
            (msg) => {
                console.error('Error updating exam:', msg);
                showError(msg);
                setLoading(false);
            }
        );
    };

    const handleCancelEdit = () => {
        setShowEditModal(false);
        setEditingExam(null);
    };

    // Group exams by date
    const examsByDate = exams
        .filter(exam =>
            (!filterExamType || exam.examType === filterExamType) &&
            (searchQuery === '' ||
                exam.courseName.toLowerCase().includes(searchQuery.toLowerCase()) ||
                (exam.department && exam.department.toLowerCase().includes(searchQuery.toLowerCase()))
            )
        )
        .reduce((acc, exam) => {
            if (!acc[exam.examDate]) acc[exam.examDate] = [];
            acc[exam.examDate].push(exam);
            return acc;
        }, {});

    return (
        <div className='page exam-timetable student-view'>
            <div className='page-header'>
                <h1 className='page-title'>
                    <span className='title-icon'><CertifiedDocument width={28} height={28} /></span>
                    Published Examination Timetable
                </h1>
                <div className='header-actions'>

                    {exams.length > 0 && (
                        <>
                            <button
                                className='btn btn-print'
                                onClick={handlePrint}
                            >
                                <Printer width={14} height={14} color="white" /> Print
                            </button>
                            <button
                                className='btn btn-download'
                                onClick={handleDownload}
                            >
                                <Download width={14} height={14} color="white" /> Download
                            </button>
                        </>
                    )}
                </div>
            </div>

            {/* Filters */}
            <div className='filter-section'>
                <div className='filter-bar'>
                    <label>Filter by:</label>
                    <select
                        value={filterDepartment}
                        onChange={e => setFilterDepartment(e.target.value)}
                    >
                        <option value="">All Departments</option>
                        {DEPARTMENTS.map(dept => (
                            <option key={dept} value={dept}>{dept}</option>
                        ))}
                    </select>
                    <select
                        value={filterSemester || ''}
                        onChange={e => setFilterSemester(e.target.value ? Number(e.target.value) : undefined)}
                    >
                        <option value="">All Semesters</option>
                        {SEMESTERS.map(sem => (
                            <option key={sem} value={sem}>Semester {sem}</option>
                        ))}
                    </select>
                    <select
                        value={filterExamType}
                        onChange={e => setFilterExamType(e.target.value)}
                    >
                        <option value="">Exam Type</option>
                        <option value="MSE I">MSE I</option>
                        <option value="MSE II">MSE II</option>
                    </select>

                    <div className="exam-search-container" style={{ marginLeft: 'auto' }}>
                        <span className="exam-search-icon" style={{ display: 'flex', alignItems: 'center' }}>
                            <div style={{ width: '16px', height: '16px' }}>
                                <SearchIcon fillColor="#64748b" />
                            </div>
                        </span>
                        <input
                            type="text"
                            className="exam-search-input"
                            placeholder="Search exams..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                    </div>
                </div>
            </div>

            {/* Timetable Display */}
            <div className='timetable-display'>
                {loading ? (
                    <div className='loading-state'>Loading...</div>
                ) : exams.length === 0 ? (
                    <div className='empty-state'>
                        <p> No published examination timetable available yet.</p>
                        <p>Please check back later.</p>
                    </div>
                ) : (
                    <div className='exam-table-container'>
                        <table className='exam-table'>
                            <thead>
                                <tr>
                                    <th>Date</th>
                                    <th>Time</th>
                                    <th>Exam Type</th>
                                    <th>Department</th>
                                    <th>Semester</th>
                                    <th>Course</th>
                                    <th>ACTIONS</th>
                                </tr>
                            </thead>
                            <tbody>
                                {Object.entries(examsByDate)
                                    .sort(([a], [b]) => a.localeCompare(b))
                                    .flatMap(([date, dateExams]) =>
                                        dateExams.map((exam, idx) => (
                                            <tr key={exam.id}>
                                                {idx === 0 && (
                                                    <td rowSpan={dateExams.length} className='date-cell'>
                                                        {new Date(date).toLocaleDateString('en-US', {
                                                            month: 'short',
                                                            day: 'numeric',
                                                            year: 'numeric'
                                                        })}
                                                    </td>
                                                )}
                                                <td>{formatTo12Hour(exam.startTime)} - {formatTo12Hour(exam.endTime)}</td>
                                                <td style={{ textAlign: 'center' }}><span className='exam-type-badge'>{exam.examType || '-'}</span></td>
                                                <td><span className='dept-badge'>{exam.department || 'N/A'}</span></td>
                                                <td><span className='sem-badge'>Sem {exam.semester}</span></td>
                                                <td>{exam.courseName}</td>
                                                <td>
                                                    <div style={{ display: 'flex', gap: '0.5rem', justifyContent: 'center' }}>
                                                        <button
                                                            className='btn-icon'
                                                            title='Edit Exam'
                                                            onClick={() => handleEdit(exam)}
                                                            style={{ width: '32px', height: '32px', padding: '6px' }}
                                                        >
                                                            <EditIcon width={16} height={16} />
                                                        </button>
                                                        <button
                                                            className='btn-icon delete'
                                                            title='Delete Exam'
                                                            onClick={() => handleDelete(exam.id)}
                                                            style={{ width: '32px', height: '32px', padding: '6px' }}
                                                        >
                                                            <Trash width={16} height={16} fill="currentColor" />
                                                        </button>
                                                    </div>
                                                </td>
                                            </tr>
                                        ))
                                    )}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>



            {/* Edit Modal */}
            {showEditModal && editingExam && (
                <div className='modal-overlay' onClick={handleCancelEdit}>
                    <div className='modal-content' onClick={(e) => e.stopPropagation()}>
                        <div className='modal-header'>
                            <h2 style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                                <EditIcon width={24} height={24} />
                                Edit Exam
                            </h2>
                            <button className='modal-close' onClick={handleCancelEdit}></button>
                        </div>
                        <div className='modal-body'>
                            <div className='form-row'>
                                <div className='form-group'>
                                    <label>Department *</label>
                                    <select
                                        value={editDepartment}
                                        onChange={(e) => setEditDepartment(e.target.value)}
                                        className='form-select'
                                    >
                                        {DEPARTMENTS.map(dept => (
                                            <option key={dept} value={dept}>{dept}</option>
                                        ))}
                                    </select>
                                </div>

                                <div className='form-group'>
                                    <label>Semester *</label>
                                    <select
                                        value={editSemester}
                                        onChange={(e) => setEditSemester(Number(e.target.value))}
                                        className='form-select'
                                    >
                                        {SEMESTERS.map(sem => (
                                            <option key={sem} value={sem}>Semester {sem}</option>
                                        ))}
                                    </select>
                                </div>
                            </div>

                            <div className='form-group'>
                                <label>Course *</label>
                                <select
                                    value={editCourse}
                                    onChange={(e) => setEditCourse(e.target.value)}
                                    className='form-select'
                                >
                                    <option value="">Select a course</option>
                                    {/* Show current course if it exists and isn't in the filtered list */}
                                    {editCourse && !subjectsDetails.some(s => s.name === editCourse && s.sem === editSemester) && (
                                        <option value={editCourse}>{editCourse} (Current)</option>
                                    )}
                                    {subjectsDetails
                                        .filter(s => s.sem === editSemester)
                                        .map(subject => (
                                            <option key={subject.name} value={subject.name}>
                                                {subject.name} ({subject.subjectCode})
                                            </option>
                                        ))
                                    }
                                    {/* Fallback: show all subjects if none match the semester */}
                                    {subjectsDetails.filter(s => s.sem === editSemester).length === 0 &&
                                        subjectsDetails.length > 0 && (
                                            <>
                                                <option disabled> All Subjects </option>
                                                {subjectsDetails.map(subject => (
                                                    <option key={subject.name} value={subject.name}>
                                                        {subject.name} ({subject.subjectCode}) - Sem {subject.sem}
                                                    </option>
                                                ))}
                                            </>
                                        )}
                                </select>
                            </div>

                            <div className='form-row'>
                                <div className='form-group'>
                                    <label>Exam Date *</label>
                                    <input
                                        type="date"
                                        value={editDate}
                                        onChange={(e) => setEditDate(e.target.value)}
                                        className='form-input'
                                        min={new Date().toISOString().split('T')[0]}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Exam Type</label>
                                    <select
                                        value={editExamType}
                                        onChange={(e) => setEditExamType(e.target.value)}
                                        className='form-select'
                                    >
                                        <option value="MSE I">MSE I</option>
                                        <option value="MSE II">MSE II</option>
                                        <option value="Semester End">Semester End</option>
                                    </select>
                                </div>
                            </div>

                            <div className='form-row'>
                                <div className='form-group'>
                                    <label>Start Time *</label>
                                    <input
                                        type="time"
                                        value={editStartTime}
                                        onChange={(e) => setEditStartTime(e.target.value)}
                                        className='form-input'
                                    />
                                </div>

                                <div className='form-group'>
                                    <label>End Time *</label>
                                    <input
                                        type="time"
                                        value={editEndTime}
                                        onChange={(e) => setEditEndTime(e.target.value)}
                                        className='form-input'
                                    />
                                </div>

                                <div className='form-group'>
                                    <label>Test Coordinator</label>
                                    <input
                                        type="text"
                                        value={editTestCoordinator}
                                        onChange={(e) => setEditTestCoordinator(e.target.value)}
                                        className='form-input'
                                        placeholder="Coordinator"
                                    />
                                </div>
                            </div>


                        </div>
                        <div className='modal-footer'>
                            <button className='btn secondary' onClick={handleCancelEdit}>Cancel</button>
                            <button className='btn primary' onClick={handleSaveEdit}>Save Changes</button>
                        </div>
                    </div>
                </div>
            )}

            <style>{`
                .student-view .filter-section {
                    background: var(--containerColor, #fff);
                    padding: 1rem 1.25rem;
                    border-radius: 10px;
                    margin-bottom: 1rem;
                    box-shadow: 0 2px 8px rgba(0,0,0,0.06);
                }
                .student-view .filter-bar {
                    display: flex;
                    align-items: center;
                    gap: 0.75rem;
                    flex-wrap: wrap;
                }
                .student-view .filter-bar label {
                    font-weight: 500;
                    color: var(--textColor2, #666);
                }
                .student-view .timetable-display {
                    background: var(--containerColor, #fff);
                    padding: 1.25rem;
                    border-radius: 10px;
                    box-shadow: 0 2px 8px rgba(0,0,0,0.06);
                }
                .student-view .loading-state {
                    text-align: center;
                    padding: 3rem;
                    color: var(--textColor2, #888);
                }
                .student-view .timetable-footer {
                    display: flex;
                    justify-content: space-between;
                    margin-top: 1rem;
                    font-size: 0.85rem;
                    color: var(--textColor2, #666);
                }
                .student-view .note {
                    font-style: italic;
                }

                /* Edit Modal Styles */
                .modal-overlay {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    background: rgba(0, 0, 0, 0.5);
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    z-index: 1000;
                    animation: fadeIn 0.2s ease;
                }

                .modal-content {
                    background: var(--containerColor, #fff);
                    border-radius: 12px;
                    width: 90%;
                    max-width: 500px;
                    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.2);
                    animation: slideUp 0.3s ease;
                }

                .modal-header {
                    padding: 1.5rem;
                    border-bottom: 1px solid var(--borderColor, #e0e0e0);
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                }

                .modal-header h2 {
                    margin: 0;
                    font-size: 1.5rem;
                    color: var(--textColor, #333);
                }

                .modal-close {
                    background: none;
                    border: none;
                    font-size: 2rem;
                    cursor: pointer;
                    color: var(--textColor2, #666);
                    width: 32px;
                    height: 32px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    border-radius: 4px;
                    transition: all 0.2s;
                }

                .modal-close:hover {
                    background: var(--hoverColor, #f0f0f0);
                    color: var(--textColor, #333);
                }

                .modal-body {
                    padding: 1.5rem;
                }

                .form-row {
                    display: grid;
                    grid-template-columns: 1fr 1fr;
                    gap: 1rem;
                }

                .form-group {
                    margin-bottom: 1.25rem;
                }

                .form-group label {
                    display: block;
                    margin-bottom: 0.5rem;
                    font-weight: 500;
                    color: var(--textColor, #333);
                }

                .form-select,
                .form-input {
                    width: 100%;
                    padding: 0.75rem;
                    border: 1px solid var(--borderColor, #ddd);
                    border-radius: 8px;
                    font-size: 1rem;
                    background: var(--containerColor, #fff);
                    color: var(--textColor, #333);
                    transition: all 0.2s;
                }

                .form-select {
                    cursor: pointer;
                }

                .form-select:focus,
                .form-input:focus {
                    outline: none;
                    border-color: var(--primaryColor, #007bff);
                    box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.1);
                }

                .modal-footer {
                    padding: 1.5rem;
                    border-top: 1px solid var(--borderColor, #e0e0e0);
                    display: flex;
                    justify-content: flex-end;
                    gap: 0.75rem;
                }

                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                @keyframes slideUp {
                    from {
                        transform: translateY(20px);
                        opacity: 0;
                    }
                    to {
                        transform: translateY(0);
                        opacity: 1;
                    }
                }
            `}</style>

            {/* Print Selection Modal */}
            {showPrintModal && (
                <div className='modal-overlay' style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(0,0,0,0.5)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 1000
                }}>
                    <div className='modal-content' style={{
                        background: 'var(--containerColor)',
                        padding: '30px',
                        borderRadius: '12px',
                        minWidth: '400px',
                        boxShadow: '0 10px 40px rgba(0,0,0,0.2)'
                    }}>
                        <h3 style={{ marginTop: 0, marginBottom: '20px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                             Print Timetable
                        </h3>

                        <div className='form-group' style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Department *</label>
                            <select
                                value={printDepartment}
                                onChange={e => setPrintDepartment(e.target.value)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Department</option>
                                {DEPARTMENTS.map(dept => (
                                    <option key={dept} value={dept}>{dept}</option>
                                ))}
                            </select>
                        </div>

                        <div className='form-group' style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Semester *</label>
                            <select
                                value={printSemester || ''}
                                onChange={e => setPrintSemester(e.target.value ? Number(e.target.value) : undefined)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Semester</option>
                                {SEMESTERS.map(sem => (
                                    <option key={sem} value={sem}>Semester {sem}</option>
                                ))}
                            </select>
                        </div>

                        <div className='form-group' style={{ marginBottom: '20px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Exam Type *</label>
                            <select
                                value={printExamType}
                                onChange={e => setPrintExamType(e.target.value)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Exam Type</option>
                                <option value="MSE I">MSE I</option>
                                <option value="MSE II">MSE II</option>
                            </select>
                        </div>

                        <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
                            <button
                                className='btn secondary'
                                onClick={() => setShowPrintModal(false)}
                                style={{ padding: '10px 20px' }}
                            >
                                Cancel
                            </button>
                            <button
                                className='btn primary'
                                onClick={handleConfirmPrint}
                                style={{ padding: '10px 20px', background: '#6366f1', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
                            >
                                 Print
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Download Selection Modal */}
            {showDownloadModal && (
                <div className='modal-overlay' style={{
                    position: 'fixed',
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: 'rgba(0,0,0,0.5)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    zIndex: 1000
                }}>
                    <div className='modal-content' style={{
                        background: 'var(--containerColor)',
                        padding: '30px',
                        borderRadius: '12px',
                        minWidth: '400px',
                        boxShadow: '0 10px 40px rgba(0,0,0,0.2)'
                    }}>
                        <h3 style={{ marginTop: 0, marginBottom: '20px', display: 'flex', alignItems: 'center', gap: '10px' }}>
                            <Download width={20} height={20} color="#28a745" /> Download Timetable
                        </h3>

                        <div className='form-group' style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Department *</label>
                            <select
                                value={downloadDepartment}
                                onChange={e => setDownloadDepartment(e.target.value)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Department</option>
                                {DEPARTMENTS.map(dept => (
                                    <option key={dept} value={dept}>{dept}</option>
                                ))}
                            </select>
                        </div>

                        <div className='form-group' style={{ marginBottom: '15px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Semester *</label>
                            <select
                                value={downloadSemester || ''}
                                onChange={e => setDownloadSemester(e.target.value ? Number(e.target.value) : undefined)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Semester</option>
                                {SEMESTERS.map(sem => (
                                    <option key={sem} value={sem}>Semester {sem}</option>
                                ))}
                            </select>
                        </div>

                        <div className='form-group' style={{ marginBottom: '20px' }}>
                            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>Exam Type *</label>
                            <select
                                value={downloadExamType}
                                onChange={e => setDownloadExamType(e.target.value)}
                                style={{ width: '100%', padding: '10px', borderRadius: '6px' }}
                            >
                                <option value="">Select Exam Type</option>
                                <option value="MSE I">MSE I</option>
                                <option value="MSE II">MSE II</option>
                            </select>
                        </div>

                        <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
                            <button
                                className='btn secondary'
                                onClick={() => setShowDownloadModal(false)}
                                style={{ padding: '10px 20px' }}
                            >
                                Cancel
                            </button>
                            <button
                                className='btn primary'
                                onClick={handleConfirmDownload}
                                style={{ padding: '10px 20px', background: '#28a745', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' }}
                            >
                                <Download width={14} height={14} color="white" /> Download
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

